INSERT INTO `equipment_data` VALUES (/*equipment_id*/101011, /*equipment_name*/"铁剑", /*description*/"四处都可买到，价格低廉的剑。\n对大多数习剑之人来说，这是他们\n第一次获得的，很重要的一把剑。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/15.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101071, /*equipment_name*/"杀手笔剑", /*description*/"外观宛如一只笔的短剑。\n攻击力虽低，\n但十分方便携带。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101101, /*equipment_name*/"木制拳套", /*description*/"能够强化拳头威力的木制武器。\n虽然做工简朴，但比起徒手攻击，\n可减轻对拳头带来的负担", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101131, /*equipment_name*/"猎人弓", /*description*/"标准的木制弓。\n主要受到猎人们的喜爱，\n无论是射程或威力，\n都十分适合用来狩猎动物。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101161, /*equipment_name*/"钢之戟", /*description*/"士兵经常使用的戟。\n攻击距离较长，且易于使用，\n所以经常被使用于战斗之中。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101191, /*equipment_name*/"单手斧", /*description*/"小型单手斧。\n本来是要做为掷斧用的，\n但由于使用上非常方便，\n因此也被拿来当成近战攻击使用。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101251, /*equipment_name*/"檀香之杖", /*description*/"以生长在神秘森林里，\n名叫阿斯特赖亚桧木的檀木\n所削制而成的法杖。\n其散发的香气能缓和伤者的心情。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101281, /*equipment_name*/"皮革工作服", /*description*/"原本是工匠在穿的围裙。\n上面的大口袋保留了原本的特征。\n新人冒险者身上得携带的物品较多，\n所以这些口袋对他们来说非常重要。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/50.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101371, /*equipment_name*/"旅行长袍", /*description*/"冒险者在最初时选择的一套装备。\n道具名称蕴含了祝福冒险者\n踏出第一步的意思，\n因此名称也成为卖点之一。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/55.0, /*atk*/0.0, /*magic_str*/1.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101401, /*equipment_name*/"木盾", /*description*/"以名为阿斯特赖亚雪松的针叶树，\n所制作而成的盾。\n因为方便加工而且便宜，\n所以经常用在学生的模拟战。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101431, /*equipment_name*/"皮靴", /*description*/"即使不是冒险者也很喜欢的基本款。\n很轻，也很适合走路，\n穿得越久就会越合脚。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101521, /*equipment_name*/"时尚贝雷帽", /*description*/"原本是画家爱用的帽子，\n但因为很适合用来遮阳跟挡风，\n所以很受新手冒险者的欢迎。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101551, /*equipment_name*/"干劲手链", /*description*/"只要装备之后，就会涌出干劲的\n不可思议的手链。\n具有提升攻击力的效果。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101581, /*equipment_name*/"守护坠饰", /*description*/"有着四叶幸运草图案的\n护身符。\n据说能够带来幸运，\n能保护持有者的安全。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/50.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101611, /*equipment_name*/"气魄手环", /*description*/"带有魔力的手链。\n戴上之后气魄就会高涨，\n有能使装备者的魔法攻击力\n提升的效果。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102011, /*equipment_name*/"齿轮剑", /*description*/"刀刃与刀柄是用齿轮接合的一把很稀有的剑。\n这是由钟表商人与铁匠所合作\n开发出来的武器。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/30.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102013, /*equipment_name*/"湛蓝之血", /*description*/"模仿某把献给贵族的名剑\n所制造出来的一把剑。\n不仅兼具了外表的美观，\n锐利度也很不错，实用性相当高。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/60.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102014, /*equipment_name*/"极光之剑", /*description*/"会发出七色光芒的美丽之剑。\n只要挥动刀身就会闪耀光芒，\n划过的轨迹宛如光于空中飞舞一般。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102041, /*equipment_name*/"弯月刀", /*description*/"有着美丽曲线的一把刀。\n过去曾有位以勇猛著称的将军，\n说这把刀沾上血液实属可惜，\n将之视为美术品珍藏的顶级武器。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/35.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102071, /*equipment_name*/"野战小刀", /*description*/"比起战场，在森林或山上遇难时，\n更能发挥它真正的价值。\n为了帮助遇难者能生存下去，\n藏有许多机关。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102101, /*equipment_name*/"皮制指虎", /*description*/"在皮革里嵌入铁块，\n强化拳头威力的武器。\n是为了希望能将武器融入自己肉体\n的武道家所设计的装备。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102131, /*equipment_name*/"水晶之弓", /*description*/"使用来自冰晶山脉的素材，\n所制作而成的一把弓。\n由于水晶可以当作魔法的触媒，\n因此可以射出夹带魔力的箭。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102162, /*equipment_name*/"三叉戟", /*description*/"拥有相当于能让整座大陆沉没于\n海里的力量，是力量与海神\n波赛顿三叉戟相近的一把长戟。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/12.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102163, /*equipment_name*/"火焰之矛", /*description*/"灌注了火精灵之力的长戟。\n宛如摇曳火焰一般的矛头，\n打倒越多魔物\n就会燃烧得越旺盛。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102191, /*equipment_name*/"金属战斧", /*description*/"钢制的战斧。\n与伐木用的斧头不同，\n以作为武器为目的而被锻造出来，\n因此具有明确的杀伤力。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/16.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102192, /*equipment_name*/"精钢斧", /*description*/"使用先进的铸造技术，\n所制作而成的战斧。\n善用它的重量来进行攻击，\n可歼灭敌人。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102221, /*equipment_name*/"绿意之杖", /*description*/"与自然有着紧密链接的精灵，\n所制作的法杖。\n除了将其当作武器使用以外，\n也会用来祈祷丰收。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/0.0, /*magic_str*/16.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102222, /*equipment_name*/"雏龙之杖", /*description*/"看似抱着宝玉的\n龙形法杖。\n虽然稀微，\n但似乎蕴藏着龙之力。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/34.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102251, /*equipment_name*/"医生掰掰法杖", /*description*/"与恢复魔法配合度很高的法杖。\n是恢复魔法师的\n爱用道具。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102252, /*equipment_name*/"独角兽的慈爱", /*description*/"使用贵重的独角兽之角制成的法杖。\n独角兽之角有着治愈的力量，\n能够提升恢复魔法的效果", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102281, /*equipment_name*/"智能之服", /*description*/"过去僧侣为传教，\n而到各地旅行时所穿着的服装。\n由于很方便行动又坚固耐用，\n所以很多魔法师也很爱穿。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102282, /*equipment_name*/"轻板甲", /*description*/"用薄板所制成的铠甲。\n拥有稳固的防御力，以及因重量较轻而方便行动是它的特征。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/320, /*require_level*/14, /*hp*/150.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102311, /*equipment_name*/"鳞甲", /*description*/"将金属或皮革以鳞片的方式贴合而成的铠甲。\n比一般的铠甲还易于活动，\n对斩击与突刺的抵抗力很强，\n相反地，较难抵御钝器的打击。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/95.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102312, /*equipment_name*/"精钢铠甲", /*description*/"使用先进的铸造技术，\n所制作而成的坚固铠甲。\n一般的攻击完全无法伤害到\n穿着这套铠甲的人。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102341, /*equipment_name*/"民俗服饰", /*description*/"融入了阿斯特赖亚传统设计的衣服。\n购买时需要有相符的身份与家世。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102342, /*equipment_name*/"忍者装扮", /*description*/"想要在暗地里行动，\n没有比这套更加适合的服装了。\n据说在某个国家，职业级间谍\n都很爱穿这套衣服。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/110.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102371, /*equipment_name*/"春日长袍", /*description*/"让人联想到春天的可爱长袍。\n作工部份相当坚实，很适合在旅行时使用，\n从外观设计到\n实用性都受到不少人的喜爱。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/65.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102372, /*equipment_name*/"北国长袍", /*description*/"为寒冷的北方国家所喜爱。\n羊毛制成的线十分温暖，\n因此用这些线所织成的斗篷能够好好地包住肩膀与手臂，\n不受到寒风的侵扰。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102401, /*equipment_name*/"塔盾", /*description*/"相当于与人等高的金属大盾。\n虽然有着压倒性的防御力，\n但另一方面，因为重量的关系而难以移动。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/15, /*hp*/135.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102431, /*equipment_name*/"小丑靴", /*description*/"以小丑的形象制作而成的\n时尚靴子。\n穿上之后，心情就会变得开朗。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/30.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102432, /*equipment_name*/"狩猎靴", /*description*/"使用栖息于高山之魔物的骨头\n所制作而成的靴子。\n那野性奔放的设计，可以唤醒野性！", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/15, /*hp*/60.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102461, /*equipment_name*/"女巫靴", /*description*/"为了采集制作魔法药的素材，\n而旅行各地的魔法师所穿着的鞋子。\n为了能承受各种严峻的环境，\n所以作工非常坚固。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/9, /*hp*/30.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102491, /*equipment_name*/"可爱报童帽", /*description*/"附有防风镜的报童帽。\n看起来像气球的剪影一样非常可爱，\n是受到兰德索尔少男少女们\n瞩目的一项道具。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102493, /*equipment_name*/"罗马军团盔", /*description*/"古代战士们所使用的头盔。\n作工非常坚固，\n即使在现代也十分合用。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102521, /*equipment_name*/"修女兜帽", /*description*/"修女所使用的头巾。\n她们能给予受了伤的人们，\n身与心之上的疗愈。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/16, /*hp*/0.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102551, /*equipment_name*/"勇气宝珠", /*description*/"能够赋予战斗所需之『仁．智．勇』\n的『勇』的宝珠。\n能够毫不畏惧地面对各种敌人，\n勇气的护身符。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/30.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102552, /*equipment_name*/"尖刺手环", /*description*/"带有魔物身上刺状鳞片的手镯。\n有提升装备者力量的效果。\n由于是用凶暴的魔物身上的鳞片\n制作而成，因此是件在市面上\n相当少见的装备。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102581, /*equipment_name*/"仁爱宝珠", /*description*/"能够提供战斗所需之『仁．智．勇』\n的『仁』之宝珠。\n祈求能够保护自己跟伙伴，\n是充满爱的心之护身符。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/55.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102582, /*equipment_name*/"胜利的编织手环", /*description*/"祈祷骑士能够平安返回，\n蕴含如此心意所制作而成的手环。\n这股心意会保护装备者，\n让防御力上升。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/17, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102611, /*equipment_name*/"魔法眼镜", /*description*/"能够提升装备者魔法攻击力的\n特殊眼镜。\n将魔法石当作素材\n制作而成。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/11.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102612, /*equipment_name*/"智能宝珠", /*description*/"能够提供战斗所需之『仁．智．勇』\n的『智』之宝珠。\n是能够让脑袋变得清晰，并能提升魔法效果的智力护身符。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102613, /*equipment_name*/"魔法单眼镜", /*description*/"高级魔法师会使用的单眼镜。\n金属部份刻有符文，\n有着能让装备者魔力上升的力量。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/17, /*hp*/0.0, /*atk*/0.0, /*magic_str*/24.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102614, /*equipment_name*/"图纹手镯", /*description*/"银制手镯上镶嵌着魔法石，\n并且刻着特殊印记的配件。\n能够活化魔法的流动，\n有着能提升用户魔力的效果。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/16, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103011, /*equipment_name*/"闪耀之剑", /*description*/"使用陨石碎片制成的单刃弯刀。\n当刀身照耀于光线之下时，\n将会像星空一般闪烁。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/90.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103012, /*equipment_name*/"龙纹剑", /*description*/"描绘那龙之吐息所制成的一把剑。\n有着较为宽阔的刀刃。比起突刺，\n更适合用来斩切。挥动它的模样，\n看起来宛若是龙在空中飞舞一般。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/115.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103013, /*equipment_name*/"羽刃之剑", /*description*/"某位魔法师为了接近战\n所使用过的剑。腕力较弱的他，\n在大鸟的羽毛上施加了魔法，\n进而造出了这把极为轻型的剑。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/37, /*hp*/135.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103041, /*equipment_name*/"月光刀", /*description*/"存在于东国的某个剑术流派中，\n授予获得宗师认可的人的刀剑。\n因寄宿着月光之力，而更能够\n琢磨精炼装备者的五感与灵力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/3000, /*require_level*/30, /*hp*/65.0, /*atk*/58.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103042, /*equipment_name*/"雷神刀", /*description*/"是某位技术甚高的武士，\n在退治雷兽时所使用的刀。\n斩除了雷兽的刀身，\n寄宿着闪电之力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/40, /*hp*/75.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103071, /*equipment_name*/"铂金小刀", /*description*/"是以高价珍贵的白金，\n打造而成的短剑。\n不少贵族把它藏在怀中以用来护身。。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103072, /*equipment_name*/"吸血鬼匕首", /*description*/"拥有意志并且不断渴望着鲜血，\n是把令人畏惧的短剑。　\n沾染上血迹而生锈的部分\n将会比平时更闪烁着恶寒之气。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103101, /*equipment_name*/"龙牙之爪", /*description*/"仿照凶恶之黑龙的下颚形状而制的钩爪。\n暗器的一种，据说以前使用这种钩爪暗杀人时，会在爪上涂上毒液，提高杀伤能力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103131, /*equipment_name*/"邱比特之弓", /*description*/"过去曾有一位男人以此弓狩猎，并将狩得的猎物赠于心上人成就了爱恋。\n他因此事而做为契机闪念，\n以爱神命名这把弓。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103132, /*equipment_name*/"恶魔之弓", /*description*/"是被人类迫害，而被追讨到地底深处的魔族所制造的弓。\n只要是踏入他们的领域的人类，\n就会葬送于这把弓下。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103161, /*equipment_name*/"骑士的骄傲", /*description*/"赠送给名誉骑士的斧戟。\n其刀刃坚实锐利不容折损，\n如同体现了为国为民而战的骑士，\n心中的那股骄傲。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/18, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103162, /*equipment_name*/"绝对之矛", /*description*/"用来刺杀的长戟。\n它的杀伤能力，\n能轻易刺穿坚固的铠甲，\n仅一击便能夺走敌人性命。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/46.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103191, /*equipment_name*/"天球斧", /*description*/"以湛蓝天体点缀的战斧。\n象征着世界本质的天体，\n能吸收天与地的能量，\n进而赋予装备者力量。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103192, /*equipment_name*/"丽花战斧", /*description*/"被赋予着华丽之花的这把斧头，\n每次挥舞，就会绽放出大朵鲜血。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103193, /*equipment_name*/"闪电之斧", /*description*/"是研究先进武器制造的某个工坊的自豪之作。\n能在斧头的内部积蓄雷电，\n并能够在攻击之际一齐放出。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/66, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/77.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103221, /*equipment_name*/"芒刺血杖", /*description*/"某个残酷的魔法师团体，\n使过的法杖。\n被害者人们的鲜血，\n将此法杖染上了鲜明的血红色。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103222, /*equipment_name*/"恶魔之角", /*description*/"据说是某位魔法师与恶魔交易之后，\n而获得的问题法杖。\n传说被击倒的敌人之魂，\n将会透过此法杖献上给恶魔。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103251, /*equipment_name*/"天球的守护", /*description*/"是将占星术为业的某个魔法师一族，\n所使用的法杖。\n据说他们能借由观测星象，\n预知未来。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/0.0, /*magic_str*/40.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103252, /*equipment_name*/"花蕾之杖", /*description*/"试图拯救人们的意念，\n使花蕾绽放，\n而那纯净的能量，\n成为了治愈的力量", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/0.0, /*atk*/0.0, /*magic_str*/53.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103281, /*equipment_name*/"时尚盔甲", /*description*/"以强大又时尚的骑士为主题，\n所制作出的铠甲。但不管再怎么盛装打扮，若不具有相应的实力，魅力定会减半。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103282, /*equipment_name*/"战阵铠衣", /*description*/"将威猛的男人全部击败，\n成为斗技场女王的女战士的铠甲。\n传闻对战对手只见到她那\n如野兽般的锐利眼光就战意全失了。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/42, /*sale_price*/3000, /*require_level*/32, /*hp*/325.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103283, /*equipment_name*/"重金属盔甲", /*description*/"使用厚实的铁板组合而成的铠甲。\n舍去轻便性，而着重于防御力。\n是使用斧头或大剑的战士们\n爱用的装备之一。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/380.0, /*atk*/17.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103311, /*equipment_name*/"华丽盔甲", /*description*/"爱好华丽的骑士，向设计师委托而制成的铠甲。\n过于华丽的缘故，在战场上格外显\n眼，在敌人眼中也正好成为了靶子。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103341, /*equipment_name*/"多层次服饰", /*description*/"在布料上覆盖一层皮革长袍的服饰。\n具有优异的防寒效果，\n适合在寒冷的地方采集素材时穿着。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/180.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/160.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103342, /*equipment_name*/"宫廷服饰", /*description*/"任职于王城内的女骑士所着装之服。\n使用了高级的皮革与黑色金属，\n在胸前还有显示骑士的身分地位的装饰。\n构思极具品味。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/210.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103343, /*equipment_name*/"玻斯菊服饰", /*description*/"是精灵的女王，在和其他种族的\n和平协定会议上所穿着的服饰。\n精灵族为以防万一，\n在女王的服饰上施加了强力魔法的薄纱。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/75, /*sale_price*/4000, /*require_level*/44, /*hp*/205.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/370.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103371, /*equipment_name*/"邱比特长袍", /*description*/"以传说中爱神的服装为形象制作的长袍。\n背上之羽，因魔法漂浮。\n让人分不出来它与真品之差。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103372, /*equipment_name*/"哲学家长袍", /*description*/"一生辅佐自幼即成为皇帝的少年的哲学家，所着的服装。\n指引皇帝踏上正途，\n他引领了国家朝着更好的未来迈进。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/245.0, /*atk*/0.0, /*magic_str*/14.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103401, /*equipment_name*/"石榴石之盾", /*description*/"骁勇果敢的将军之盾。\n他只要一站上战场，\n亲自率兵破军杀敌，\n引领军队迎向胜利。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/290.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103431, /*equipment_name*/"忍者鞋", /*description*/"穿上之后可以消除脚步声的特殊靴。\n传说是东国间谍所穿的靴子，\n但有关于他们的任何情报，\n一直都是个谜。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/3.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103432, /*equipment_name*/"击剑之靴", /*description*/"为了使用西洋剑的剑士们，\n所制成的靴子。\n相当轻盈，是对于比起力量更重视机动性的细剑士，最合适的一件装备。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/3000, /*require_level*/35, /*hp*/115.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/4.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103461, /*equipment_name*/"女巫宫廷靴", /*description*/"魔法师的正式服装之一的靴子。\n附有无论走在任何地方\n都不会脏掉的魔法。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103491, /*equipment_name*/"黑龙头盔", /*description*/"信仰曾存在的黑龙的骑士团所用的头盔。\n他们为了供奉活祭品给龙，不断夺走无辜的人的性命。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/35, /*hp*/235.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103521, /*equipment_name*/"女巫发箍", /*description*/"魔法师的一家人，赠送给为修行\n而离家的女儿的发箍。\n是母亲以自己的魔力，以及爱所制成。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103522, /*equipment_name*/"女巫帽", /*description*/"在传统的魔法师的帽子上\n加了能辅助魔力的配件。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/26.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103551, /*equipment_name*/"蛋白石耳环", /*description*/"蕴藏魔力的\n蛋白石耳环。\n能让装备者从身体内部涌出活力，\n使功击力提升。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/33.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/90.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103552, /*equipment_name*/"狮鹫之羽饰", /*description*/"以上半身为鹰鹫，下半身为猛狮的\n格里芬之羽所制成的羽饰。\n因而寄宿着狰狞猛兽之力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/235.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103553, /*equipment_name*/"绯红钻石", /*description*/"世界上极为罕见的绯红色钻石。\n据说是封进了龙的火焰，\n装备后即犹如\n获得烈火一般的力量。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/70.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103581, /*equipment_name*/"绿宝石耳环", /*description*/"蕴藏魔力的\n绿宝石耳环。\n能给予装备者不屈不挠的精神力，\n使得防御力进而上升。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103582, /*equipment_name*/"八芒星坠饰", /*description*/"八芒星象征无限循环，\n能够于大自然中吸收\n八种不同的元素。不断的守护着装备者。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/37, /*hp*/0.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103583, /*equipment_name*/"狮子王的守护", /*description*/"曾经被称为狮子王的大王之物。\n生涯大半皆活跃于征战之中的他，\n那份骁勇善战，使大多数的人民\n对他抱持着敬畏之心，", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/46, /*hp*/270.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103611, /*equipment_name*/"绿松石耳环", /*description*/"蕴藏魔力，\n以绿松石作成的耳环。\n能让装备者得到智能，\n使魔法攻击力上升。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/30.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103612, /*equipment_name*/"珍珠耳环", /*description*/"蕴藏魔力的\n珍珠耳环。\n能增加装备者的集中力，\n进而提升魔力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/36, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103613, /*equipment_name*/"魔法使眼镜", /*description*/"贤者在探索龙脉时会装备使用。\n会于研究大地中流动的气脉，\n或是调查大陆的环境与状态时使用。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/41, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/0.0, /*magic_str*/39.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/35.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103614, /*equipment_name*/"月桂树的悲凄", /*description*/"精灵达芙妮将自身姿貌，\n转化为月桂树。传闻深爱着她的太阳神．阿波罗，\n将月桂树叶做为永恒的爱之印记，\n一直配戴在身上。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104011, /*equipment_name*/"天使之剑", /*description*/"据说是大天使所使用过的\n白银之剑。\n具备将邪恶的存在消除的\n退魔之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/165.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104012, /*equipment_name*/"太阳剑", /*description*/"拥有太阳神．阿波罗的保护之力的剑。得到了此剑的人物，\n被誉为太阳王，并且得到的繁荣像名留青史一般，永远留存。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/70.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104013, /*equipment_name*/"冰之大剑", /*description*/"能于斩击的同时，\n一并发动冰之魔法来讨伐敌人。\n因蕴含着冰之妖精的魔力，\n总是带着阵阵寒气。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104014, /*equipment_name*/"浑沌之剑", /*description*/"吸取了上万活人鲜血的妖剑。\n只要被诅咒的话就会化为渴求鲜血\n的傀儡，若能驾驭它的话，就可获得\n如钢铁般的力量。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/140.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104041, /*equipment_name*/"妖蝶之刀", /*description*/"一把沉睡在森林遗迹最深处的刀剑。\n传闻中被此剑所选中之人，\n将会受到蝴蝶的指引，\n而得以抵达它所在之处。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/80.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104042, /*equipment_name*/"光辉之剑", /*description*/"接到光之女神．阿格莱亚神谕的\n刀匠，耗费了一生而锻造的光之剑。以人类得以对抗魔神之武器为名，\n由威震八方的勇者持有。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/192, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/10.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104043, /*equipment_name*/"鹰神之煌剑", /*description*/"可支配广阔天际的崇高的鹰神之剑。\n振臂一挥，就宛若锁定猎物的老鹰，\n以魔力加速的那锋刃，\n将会分毫不差地朝敌人的要害突击。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104044, /*equipment_name*/"凤凰刀", /*description*/"出现于日出之处并寄宿有凤凰神\n之力的刀。缠绕之火会随意志\n强度来变化，若是拥有强韧意志之人，\n则会化为可融化铁的烈火。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104071, /*equipment_name*/"秘宝匕首", /*description*/"传闻为某个矮人的名匠所造，\n且献给了人类之王的短剑。\n采用非现存的矿物的未知素材。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104072, /*equipment_name*/"公主怀剑", /*description*/"那历史上存续最悠久的小国王族，\n所相传的短刀。代代皆以王女继承，\n她们退去降临的灾祸魔恶，\n持续守护着国土的安宁。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/50.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/300.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104073, /*equipment_name*/"暗黑物质短剑", /*description*/"为肃清双手沾染上禁忌，无法无天的邪恶魔法师，而制作的短剑。施加了能够封印魔法发动的术式。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104074, /*equipment_name*/"独角兽匕首", /*description*/"只有天真无邪的女性可使用的短剑。\n由于可拒各种污秽于门外，\n因此抱有邪恶意志之人\n甚至无法触碰。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/160, /*sale_price*/18000, /*require_level*/59, /*hp*/100.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104101, /*equipment_name*/"火焰拳套", /*description*/"以炼狱之焰锻造而成的\n深红拳套。\n从拳尖散射肆虐的业火，\n能将眼前的敌人彻底烧之殆尽。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/50, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104102, /*equipment_name*/"狮子王手套", /*description*/"被誉为狮子王的名君，\n曾使用过的黄金拳套。传说此拳套能赋予用户\n无限的力量，以及一骑当千的战力。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/81.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/12.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104103, /*equipment_name*/"暴雪之爪", /*description*/"孤军一人便坏灭万人大军的冰之女\n神的魔力，所封印的钩爪。\n那刺骨的寒风，将如同遭遇暴雪般受冰寒的敌人的战意给彻底抹灭。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/90.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104104, /*equipment_name*/"苍雷拳", /*description*/"受勇猛的雷神所加护的护手。\n放出的苍之雷击不只可一瞬间\n电焦敌人，甚至连雷云\n都能够呼唤过来。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104131, /*equipment_name*/"雷光弓", /*description*/"英雄为击倒那食人海龙，\n将雷之力封存于此弓之中。\n传说那被电光缠绕着的箭矢，\n仅仅靠一击便将那海龙给埋葬。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/145, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104132, /*equipment_name*/"天使之弓", /*description*/"古代的末日之战时，\n大天使与恶魔一战时所持的大弓。传闻满弦放出的一矢，\n便能够贯穿地狱直抵冥河。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/65.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104133, /*equipment_name*/"月亮女神之弓", /*description*/"寄宿着狩猎女神的庇佑的弓。\n拥有对猎物百发百中\n和破魔效果的箭矢，\n仅一射便能彻底扭转战局。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104134, /*equipment_name*/"精灵树之弓", /*description*/"以精灵树为素材所制成的弓。\n弓内蕴涵精灵界的神圣魔力，\n击出的箭拥有可贯穿\n各种敌人的威力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/60.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104161, /*equipment_name*/"绯龙戟", /*description*/"以栖息于火山周遭的\n龙背上长的针刺所制成的战戟。\n那锋利的刃尖，因长年寄宿于龙之体内，而蕴含着火炎之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104162, /*equipment_name*/"皇后之矛", /*description*/"传说中支配整个大陆的女王所使用的大战戟。她拥着睿智才华，在长年征战之下，\n以此战戟之力将整座大陆囊括怀中。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104163, /*equipment_name*/"暴风权杖", /*description*/"传说中的枪骑士所持有的名戟。\n只要一挥舞，那真空之刃便会\n卷起旋风，无须以刃直接碰触\n也能将敌人一网打尽。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/205, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/5.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104164, /*equipment_name*/"盖亚战斧", /*description*/"受大地母神所加护的枪。\n枪身绝不会断裂缺损或粉碎。\n那份强韧会寄宿在持有者体内，使\n臂力得到惊人的成长。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104191, /*equipment_name*/"紫龙斧", /*description*/"各地集结的英杰们齐心，将\n威胁世间的邪龙封印于此斧之中。\n寄宿着龙之魂的这把斧头，\n能够释放出造成灾害祸殃的能量。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/91.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104192, /*equipment_name*/"地狱火之斧", /*description*/"封印了地狱之火的双刃斧。那外观样貌如同鲜血般赤红，\n高举一斩那红莲之火将会袭向敌人，\n将对方连骨带肉给烧之殆尽。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/97.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104193, /*equipment_name*/"战女王之烈斧", /*description*/"总是君临于最前线的女王的战斧。\n虽拥有着超乎想像的重量，但被此\n斧所认定之人将能轻易操使。此斧能一击打破牢固的城门。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/114.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104194, /*equipment_name*/"水灵破坏者", /*description*/"水之精灵注入精灵之力的斧头。\n虽然很轻，但却相当锋利，\n即使只是接触到就有可能使得矿物\n一分为二。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104221, /*equipment_name*/"狂怒之杖", /*description*/"因妻子被杀害，\n而化为复仇之鬼的魔法师所持的法杖。\n他将自身的愤恨注于此杖之上，\n把所有与妻之死有关之人抹杀殆尽。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/0.0, /*magic_str*/91.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104222, /*equipment_name*/"太阳之杖", /*description*/"远古的贤人们，集结太阳的碎片所制成的神秘之杖。\n传闻被此法杖认定的人，\n将会被赋予全知全能之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104223, /*equipment_name*/"司法之神的锡杖", /*description*/"据说是司掌正义、和平与真实之神，\n所持有的锡杖。\n传闻得到此锡杖之人，\n将会被被托付神之示谕。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/104.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104224, /*equipment_name*/"大恶魔法杖", /*description*/"寄宿有高等恶魔之魂的杖。\n对敌人毫不留情，\n只要施展击倒敌人的魔法\n便能发挥其真正价值。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/0.0, /*magic_str*/108.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104251, /*equipment_name*/"月光之杖", /*description*/"据说是月之女神在治疗虔诚信徒的疾病时，所使用过的长杖。\n传说畏惧着女神之力的死神，\n将此长杖给埋藏于下界之中。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/0.0, /*magic_str*/73.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104252, /*equipment_name*/"生命之杖", /*description*/"以被誉为生命之树的巨大树枝，\n所制造而成的\n奇迹之杖。传闻中只要一挥，连腐朽之木也能\n长出新芽，轻触于地则能涌泉。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/67.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104253, /*equipment_name*/"不死鸟之杖", /*description*/"拥有不死鸟庇护的神圣之杖。\n能获得无穷的魔力，\n因魔力不会用尽，\n可无止境地尽情施展魔法。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104254, /*equipment_name*/"大天使之杖", /*description*/"受满溢慈爱的天使所加护的杖。\n由于可回应怜爱他人的情感，\n因此帮助同伴的魔法的效果\n将明显地提升。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/0.0, /*magic_str*/86.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104281, /*equipment_name*/"隐身铠衣", /*description*/"施加了隐身魔法的特殊铠甲。\n据说是某国的公主因陷入了一场不被允许的恋爱，\n在与恋人私奔时使用的铠甲。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104282, /*equipment_name*/"秘银胫甲", /*description*/"当富有银之光辉与钢之强悍，\n的稀有金属，\n碰上当代首屈一指的名匠，\n则诞生出兼具外貌与强韧的至高无上的铠甲。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/172, /*sale_price*/10000, /*require_level*/52, /*hp*/250.0, /*atk*/25.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104283, /*equipment_name*/"十字军板甲", /*description*/"以神之名义驱逐了异教徒，\n高明远播的勇者所穿着的铠甲。\n被赋予着神的恩惠与庇祐之铠，\n唯有拥有纯净之魂的人才能获得。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/200, /*sale_price*/15000, /*require_level*/54, /*hp*/150.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104284, /*equipment_name*/"绯红铠甲", /*description*/"由黑暗女帝赠送，为护国而战的\n骑士公主所持有的红色铠甲。\n其神圣光辉使敌人胆寒，\n能给予民众勇气。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/160.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104311, /*equipment_name*/"天使盔甲", /*description*/"天使们在征战时所使用的铠甲。\n低等级恶魔连接近此铠甲都办不到，\n只是被那白银所反射的光芒给照耀，\n就会被消灭。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104312, /*equipment_name*/"朱红铠甲", /*description*/"以单骑赴敌而献上自身之命，\n那疯狂战士之魂所寄宿的铠甲。\n着此铠甲于身者，\n将能得到那战鬼之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/10000, /*require_level*/52, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/400.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104313, /*equipment_name*/"紫罗兰盔甲", /*description*/"被视为战场的死神而遭人畏惧的大将军，\n所配备的美丽铠甲。\n敌军兵将光是看见那菫色的甲胄，便将无心再战且落荒而逃。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/252, /*sale_price*/15000, /*require_level*/54, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104314, /*equipment_name*/"龙威战甲", /*description*/"屠龙英雄所爱用的重铠甲。\n以龙为概念所设计的特厚装甲，\n据说能够防护斩钉截铁的爪击，\n以及灼热的龙之吐息。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/330, /*sale_price*/18000, /*require_level*/58, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104341, /*equipment_name*/"月下之法衣", /*description*/"神官受王之敕令而赌上性命，\n所制作的绚丽法衣。\n传闻因此衣是于月光中缝制而成，\n只要被月光照耀，便会发出七彩光芒。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/190, /*sale_price*/7200, /*require_level*/51, /*hp*/360.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/325.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104342, /*equipment_name*/"隐者服饰", /*description*/"世俗誉之为隐士，窥视了魔道\n深渊之人所着的衣物。\n以渗入魔力的丝与线\n缝制而成。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/10000, /*require_level*/52, /*hp*/180.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104343, /*equipment_name*/"苍天长袍", /*description*/"若为了人民，即便是对神也会刀剑相向，\n抱有义侠之志的术士穿的长袍。\n蕴含着至高无上光辉的天之庇护，\n将会赋予装备者钢铁般的意志。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/270, /*sale_price*/15000, /*require_level*/54, /*hp*/200.0, /*atk*/45.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104344, /*equipment_name*/"暗黑惧袍", /*description*/"黑暗女帝身穿的衣裳。\n潜伏如黑暗的意念，\n能够魅惑任何魔物，\n施予众人恐怖。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/330, /*sale_price*/18000, /*require_level*/58, /*hp*/200.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/250.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104371, /*equipment_name*/"大魔导师长袍", /*description*/"将人生奉献给魔道的大魔导师，\n所穿过的长袍。\n因为曾经的持有者的强大力量，\n依旧持续散发着不祥的魔力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104372, /*equipment_name*/"圣者长袍", /*description*/"与天使们一同\n与恶魔对峙的圣者，\n所身穿的长袍。\n这份崇高已达神话的领域。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/141, /*sale_price*/10000, /*require_level*/52, /*hp*/240.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104373, /*equipment_name*/"翠绿灵衣", /*description*/"据说是住在神树森林里的\n伟大贤者身穿的长袍。\n蕴含有神灵之力，穿着时\n可以防备任何邪恶魔力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/321, /*sale_price*/18000, /*require_level*/58, /*hp*/200.0, /*atk*/0.0, /*magic_str*/25.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/3.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104401, /*equipment_name*/"女帝之盾", /*description*/"只有女王完全信赖的进卫兵，\n才能得到的盾牌。\n因寄宿着绝对的守护之力，\n无论魔法还是诅咒皆会反弹。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/166, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/4.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104402, /*equipment_name*/"守护者之盾", /*description*/"于乱世中的小国，以生命守护王女之命的骑士的盾。\n尔后王女登基为女王，\n于她身边的那名骑士，被以救国英雄知名称赞。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/246, /*sale_price*/15000, /*require_level*/55, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104431, /*equipment_name*/"圣骑士胫甲", /*description*/"接受了神圣之力所庇祐，\n圣骑士们所穿着的足甲。\n为守护人民们宝贵的性命，\n他们总是命悬一线地与魔物战斗。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/9.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/6.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104432, /*equipment_name*/"天使之靴", /*description*/"在神话中曾出现过的神圣脚步防具。\n不论任何攻击皆无法靠近，\n即使于热炎之中，\n也能毫发无伤步行。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/140, /*sale_price*/10000, /*require_level*/52, /*hp*/220.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104433, /*equipment_name*/"妖精护靴", /*description*/"受太古妖精所加持的腿甲。\n除了巨大的灵力，\n还有驱魔的秘银点缀在周围，\n使破魔之力大增。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/290, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/38.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104461, /*equipment_name*/"巫师靴", /*description*/"只有被王族认可的魔法师，\n才会获赠的特别道具。\n只有于此行百年的精灵职人，\n才有办法做得出来的靴子。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104462, /*equipment_name*/"死灵术士之靴", /*description*/"被称为司掌黄泉者而遭人畏惧，\n是稀有的死灵术士所钟爱的靴子。\n只是听闻到那靴音，精气便会被\n夺去，且将隶属于装备者麾下。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/220, /*sale_price*/15000, /*require_level*/55, /*hp*/100.0, /*atk*/0.0, /*magic_str*/15.0, /*def*/1.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104463, /*equipment_name*/"宝瓶之靴", /*description*/"蕴含掌管水元素的\n四大精灵之力的美丽靴子。\n据说借由伟大精灵之力，\n能够激发装备者的魔力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/40.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/7.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104491, /*equipment_name*/"羽翼圆舞曲", /*description*/"被称为是空中舞者的半神半人骑士\n所使用的头盔。\n据说他会在空中来回飞舞，\n玩弄敌人于股掌之间。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/135, /*sale_price*/7200, /*require_level*/52, /*hp*/490.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104521, /*equipment_name*/"巫师兜帽", /*description*/"是受国家认可，\n身为最高级魔法师的证明。\n拥有此兜帽的人，\n将被允许立处于国王的身边。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/0.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104522, /*equipment_name*/"法王兜帽", /*description*/"历代的法王出征时，\n会配戴的头盔。\n因被赋予着破魔的效果，\n轻而易举的识破幻术。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/10000, /*require_level*/52, /*hp*/90.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104523, /*equipment_name*/"福音之冠", /*description*/"某个魔法王国的女王所身着过，\n拥有显赫正统背景的冠帽。以增强自身魔力的那份力量，\n女王为王国长久以来带来了秩序。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/246, /*sale_price*/15000, /*require_level*/55, /*hp*/100.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104551, /*equipment_name*/"元素之心", /*description*/"统整四大精灵的魔力，\n并且封印之的特殊矿石。\n稀世的炼金术士\n完成的一大发明品。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104552, /*equipment_name*/"太阳的护身符", /*description*/"使用了接受过太阳神庇护的宝玉，\n为了与魔抗衡的战士们所制的首饰。\n就算与强大的敌人正面对峙，\n也能不陷入恐慌状态与之战斗。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/0.0, /*atk*/70.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104553, /*equipment_name*/"焰帝的戒指", /*description*/"戒指上所镶嵌的红宝石中，寄宿着\n自创世之时便燃烧至今的火焰。\n配戴后将能够让可烧毁万物\n的火焰缠绕于身上。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/200.0, /*atk*/74.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104554, /*equipment_name*/"猫神的项坠", /*description*/"被认为是由猫神所生出的宝石。\n配戴者可获得惊人的视力\n与反射神经，因此绝不会\n看漏任何人，或让其逃跑。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/0.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/8.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104581, /*equipment_name*/"替身手环", /*description*/"君临了千秋万世的皇帝，\n命人锻造而出的传奇的魔导具。\n传闻会替装备者\n承受降下的灾厄。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/240.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/5.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104582, /*equipment_name*/"月之手链", /*description*/"以封存了月之魔力的水晶，\n所制成的手链。\n持续散发的灵妙之光成就了庇护，\n无论何等诅咒都能轻易驱散。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104583, /*equipment_name*/"圣信徒的项坠", /*description*/"只有被圣灵所认可之人才被允许\n配戴的项链。\n借由圣灵无限的慈爱\n可让配戴者免于威胁。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/200.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104611, /*equipment_name*/"新月的叹息", /*description*/"因受计谋而以弓伤了心爱之人，\n那月之女神所持有的护身符。\n下弦月的魔力满负着哀戚，\n能够将飞来之矢给弹落。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/100.0, /*atk*/0.0, /*magic_str*/60.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/6.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104612, /*equipment_name*/"龙之泪", /*description*/"是龙之泪超越悠久时空而化为的结晶，\n一种极为稀少的矿石。\n直接撼动装备者的魂魄，\n传达了那寄宿于内的龙之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/120.0, /*atk*/0.0, /*magic_str*/70.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104613, /*equipment_name*/"大贤者的宝珠", /*description*/"将许多战争引向胜利的大贤者，\n把其所拥有的所有魔力全数注入\n的水晶球。拥有者将被授予引向光荣\n的聪明才智。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/0.0, /*atk*/0.0, /*magic_str*/80.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/16.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105011, /*equipment_name*/"雷霆神锋", /*description*/"由雷神劈在地上的雷霆之怒\n所生的大剑。一挥动就能产生\n劈开长空的闪电，据说任何敌人\n都能扫除。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105012, /*equipment_name*/"天开之圣剑", /*description*/"据说是属于前往圣域的关键物品，\n神圣的大剑。\n凭着剑身上的神气护佑、\n能令佩戴者消除邪恶的存在。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/72, /*hp*/120.0, /*atk*/115.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105013, /*equipment_name*/"罪过之大剑", /*description*/"不斩人而斩除罪业的大剑。\n据说被此剑所斩的罪人将不会\n受伤，但其代价是将余生奉献给\n赎罪一途。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/475, /*sale_price*/30000, /*require_level*/80, /*hp*/140.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/8.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105041, /*equipment_name*/"百华白樱刀", /*description*/"具备有映照洁白、盛开的樱花般\n美丽的刀。当装在柄上的铃一响，\n就如繁花落尽的轻盈，\n能将来犯的敌人一一斩除。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/118.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105042, /*equipment_name*/"流祸之苍刃", /*description*/"在天地异变之中沉没的帝国魔法剑。\n剑刃上刻印着国家的历史。\n受到水之精灵宠爱的刀身，\n能挥洒出流水一般变幻自如的斩击。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105043, /*equipment_name*/"妖刀血鸦", /*description*/"受诅咒而时常嗜血的刀。\n据说此刀若判断这一斩不值得\n将会咒死用户，并使其化成\n笼罩于刀身的妖气。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/592, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/125.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105071, /*equipment_name*/"翡翠匕首", /*description*/"受到丰饶之神所庇佑的匕首。\n那光彩鲜漾的刀刃\n据说能吸收大自然能量，\n从而挥洒出鲜丽的斩击。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105072, /*equipment_name*/"熔岩烈锋", /*description*/"寄宿着火之精灵的短剑。\n以堪比熔岩般灼热的刀身\n来回刺出的一击，令接触到的敌人\n在那瞬间烧熔。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/405, /*sale_price*/27000, /*require_level*/72, /*hp*/40.0, /*atk*/115.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105073, /*equipment_name*/"常春国短剑", /*description*/"由妖精王族代代相传的护身剑。\n剑刃上刻有妖精族秘传的魔法，\n具备令用户驱散邪恶气息的\n力量。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105101, /*equipment_name*/"月之拳套", /*description*/"绽放神圣光华，\n造型如月的格斗武器。\n拳上蕴含月光精华，\n据说能向敌人轰出强烈一击。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/6.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105102, /*equipment_name*/"深红之爪", /*description*/"寄宿着魔种的深红色钩爪。\n极度残暴的一击赋予任何活物的\n只剩绝望，据说能给予对阵的敌人\n绝对的死亡。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105103, /*equipment_name*/"冥河之爪", /*description*/"以冥府之冰精炼而成的冰爪。\n被爪割裂的目标将在瞬间冻结，\n成为无法说话的冰冻雕像，\n最后变为冥府的居民。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/470, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105131, /*equipment_name*/"白银大翼弓", /*description*/"以白银之鸟翼为模样的弓。\n射出的箭有如飞鸟翔空，\n有如穿梭而过的一击，\n能够贯穿任何魔物。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/245, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105132, /*equipment_name*/"深渊之弓", /*description*/"受到魔族女王加持的妖弓。\n射出的箭会带有邪异的魔力，\n如同在黑暗之中乱窜，\n从难以预测的地方射向敌人。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/7.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105133, /*equipment_name*/"红天弓", /*description*/"当传说的弓手在弓折箭尽的关头\n从天而降获得的神圣之弓。\n能承受任何速射，弓手一旦瞄准，\n箭矢绝对会命中。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/126.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105161, /*equipment_name*/"零霜之枪", /*description*/"据传被封印在传说中的\n冰狱的冰霜之枪。\n被其一枪刺中的目标，\n据说会在哀嚎中逐渐冰封。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/98.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105162, /*equipment_name*/"雄风刺针", /*description*/"接受风神的恩惠，\n浑身正气的大枪。\n刺出的一击是\n眼力无法捕捉的锐风，\n能将一切吹散。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/305, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/92.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105163, /*equipment_name*/"护天之圣枪", /*description*/"由守护圣域者使用的神圣之枪。\n就算枪头不用对准敌人，\n倒在脚下的邪恶尸骸就会\n延绵不绝。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/102.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105191, /*equipment_name*/"死灵之斧", /*description*/"封入有冥界之力的斧。掌管死亡\n的使者在为亡者引路时所使用，\n据说在其钝重之声响起的同时，\n就能将生者领往冥界。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/400, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/1.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105192, /*equipment_name*/"神判之圣斧", /*description*/"是天界施行裁决时所使用的\n神圣刃斧。\n目标的罪业越大、则威力越大，\n显现神祇的威严。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105193, /*equipment_name*/"盖亚之斧", /*description*/"由古代女战士挥动的战斧。\n此斧所屠杀之人，将被接引至\n众神所居住的土地肥沃的\n理想国去。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/430, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105221, /*equipment_name*/"异域法杖", /*description*/"封入有异界魔力的杖。\n蕴含有爆发性的威力，\n据说若不是具备相当实力的人，\n无法解放其威力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/3.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105222, /*equipment_name*/"创世法杖", /*description*/"是世纪级大魔导士的爱用法杖。\N据说蕴含创造天地的至理，\n光是一挥就能令魔物\n点滴无存地消逝。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/0.0, /*magic_str*/110.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/12.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105223, /*equipment_name*/"克劳诺斯雷杖", /*description*/"将轰透天际的雷电之力封入的\n神圣之杖。虽是人手难以负荷的\n霸烈之杖，但每用一次都能把\n任何敌人烧毁。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/525, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/118.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/14.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105251, /*equipment_name*/"焰灵法杖", /*description*/"由爱所生的火焰女神所加持的杖。\n如燃烧般溢出的爱之光辉，\n据说不只能治愈受伤的伙伴，\n还能疗愈心灵。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/95.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105252, /*equipment_name*/"神花之圣杖", /*description*/"因慈爱的精神而\n衍生出灵花结晶的圣杖。\n蕴含着无穷无尽的治愈能力，\n见杖如见女神亲临。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/300, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/2.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105253, /*equipment_name*/"亚特兰提斯杖", /*description*/"蕴含如母亲般之大海生命的圣杖。\n据说此杖一挥，无论是\n任何伤患或疾病，都能够\n立刻痊愈。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/460, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105281, /*equipment_name*/"魅影铠甲", /*description*/"令见者迷惑心智的魔性铠甲。\n只要看到散发出的袅袅紫烟，\n就会被逃不了的深层幻觉\n给迷惑。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/349, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/32.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105282, /*equipment_name*/"九天之铠", /*description*/"以失传的太古技术制作的铠甲。\n赋予穿戴者隐形的翅膀，\n在战场上纵横无阻、穿梭自如。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/392, /*sale_price*/27000, /*require_level*/77, /*hp*/80.0, /*atk*/42.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105311, /*equipment_name*/"苍辉之铠", /*description*/"由青翠透明的结晶制成的铠甲。\n这种前所未见的结晶，\n以难以加工的强度著称，\n连想要划伤表面都做不到。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/511, /*sale_price*/24000, /*require_level*/70, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105312, /*equipment_name*/"圣樱之铠", /*description*/"是为拯救濒临危机的祖国而奋起\n的勇猛少女所穿着的铠甲。\n具备不可思议的守护之力，\n能够反弹任何攻击。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/401, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/11.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105341, /*equipment_name*/"神盾之衣", /*description*/"是毕生未尝一败的女骑士\n所穿着的战衣。据传只要穿上身一次，\n在任何战局都可以逆转成\n必胜的战果。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/509, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105342, /*equipment_name*/"少女之服", /*description*/"只有一尘不染的少女，\n才能穿上的神圣服装。\n随着少女奉献出无垢的祈愿，\n能发挥出绝大的破邪之力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/355, /*sale_price*/27000, /*require_level*/77, /*hp*/160.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105371, /*equipment_name*/"纯洁的巫女服", /*description*/"是将一切献给神的巫女\n所穿着的装束。据传能令穿戴者集神之宠爱于一身，\n得到不屈服于魔威的\n守护之力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/365, /*sale_price*/24000, /*require_level*/70, /*hp*/120.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105372, /*equipment_name*/"女帝之袍", /*description*/"是由过往的伟大女帝所穿着的\n长袍。每一缕丝线都贯注了\n女帝的魔力，令穿戴者能够\n防护任何魔法。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/43.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105401, /*equipment_name*/"星咏之圆盾", /*description*/"刻有天体图的圆盾。\n寄宿有天上众星的力量，\n能够抵挡卸除任何攻击，\n正是受到星光引导与呼唤。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/446, /*sale_price*/24000, /*require_level*/70, /*hp*/0.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105431, /*equipment_name*/"炼狱之胄", /*description*/"称霸各地的战姬被敌剑斩倒\n为止，一直穿着的军靴。\n差一步而无法得到世界的遗憾，\n使其蕴含着绝大的魔力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/77, /*hp*/60.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/6.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105521, /*equipment_name*/"忘哭之冠", /*description*/"灭亡于过去、已被遗忘的\n某国公主所戴的头冠。令下一个持有者也持续\n感应到深切的哀痛之外，\n并赋予宏大的魔力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/0.0, /*magic_str*/48.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105522, /*equipment_name*/"白祈之圣冠", /*description*/"受到庶民广泛尊敬的圣女\n所赠送的冠冕。若穿戴者的\n心地清朗，就能发挥辟退恶贼的\n力量。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/22.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/12.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105551, /*equipment_name*/"千岁耳环", /*description*/"从地龄数千年的地层\n发掘出的魔石耳环。\n其蕴藏浩大的自然之力，\n赋予佩戴者辽阔大地的加护。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/70.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0);
