INSERT INTO `equipment_data` VALUES (/*equipment_id*/105581, /*equipment_name*/"精灵王护石", /*description*/"受精灵王祝福的耳饰。\n据说有涵盖一整座森林的\n守护之力，能够辟除任何灾厄。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/200.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105582, /*equipment_name*/"人鱼公主的灵泪", /*description*/"是结晶化的人鱼公主流泪\n形成宝玉而制成的首饰。\n牵挂永久的思念持续放出青色光华，\n能拒绝任何诅咒。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/60.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/2.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
