INSERT INTO `unit_data` VALUES (/*unit_id*/104701, /*unit_name*/"纯", /*kana*/"纯", /*prefab_id*/104701, /*is_limited*/0, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/135, /*atk_type*/1, /*normal_atk_cast_time*/2.125, /*cutin_1*/104701, /*cutin_2*/0, /*guild_id*/2, /*exskill_display*/0, /*comment*/"【物理】在最前方施展完全防御技能的骑士团长。\n必杀技则可展开任何伤害都能\n吸收的屏障。尤其以长期战中的压倒性防御能力自豪。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104801, /*unit_name*/"美冬", /*kana*/"美冬", /*prefab_id*/104801, /*is_limited*/0, /*rarity*/2, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/420, /*atk_type*/1, /*normal_atk_cast_time*/2.19, /*cutin_1*/104801, /*cutin_2*/0, /*guild_id*/8, /*exskill_display*/0, /*comment*/"【物理】位于中卫，拥有晕眩与恢复的技能的佣兵战士。\n拥有高度防御能力及自我恢复能力，\n能使中卫的防守更为坚固，支撑整个队伍。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
